import React, { Component } from 'react';
import {connect} from 'react-redux';
import { NavPane, NavPaneItem, Text } from 'react-desktop/windows';
import ChartShow from './chartshow';
import Historydata from './historydata';
import { Button } from 'antd';
import {
  getrealtimedata_request,
  querydata_request,
} from '../actions';
import _ from 'lodash';
import ChartXY from './chartxy';

class MainPage extends Component {
    static defaultProps = {
        color: '#FFFFFF',
        theme: 'dark'
    };

    constructor() {
        super();
        this.state = {
            selected: '工作台'
        }
    }

    render() {
        return (
            <NavPane
                openLength={200}
                push
                color={"#66ACDA"}
                theme={this.props.theme}
                >
                {this.renderItem1('工作台', <ChartShow />)}
                {this.renderItem2('历史查询', <Historydata />)}
            </NavPane>
        );
    }

    renderItem1(title, content) {
        const {createtimestring,currealtimedatalist} = this.props;
        let lines = [];
        _.map(currealtimedatalist,(currealtimedata)=>{
          const {rawdata_55,rawdata_ee} = currealtimedata;
          const data_55 = _.slice(rawdata_55,50,250);
          const data_ee = _.slice(rawdata_ee,50,250);
          let data55 = [];
          let dataee = [];
          _.map(data_55,(v,index)=>{
            data55.push({x:index,y:v});
          });
          _.map(data_ee,(v,index)=>{
            dataee.push({x:index,y:v});
          });
          lines.push({
            data:data55,
            color:'#' + parseInt(Math.random() * 0xffffff).toString(16)
          });
          lines.push({
            data:dataee,
            color:'#' + parseInt(Math.random() * 0xffffff).toString(16)
          });
        });

        return (
            <NavPaneItem
                title={<span style={{color: "#FFF"}}>{title}</span>}
                icon={this.renderIcon(title)}
                theme="light"
                background="#0075c1"
                selected={this.state.selected === title}
                onSelect={() => this.setState({ selected: title })}
                padding="10px 20px"
                color = {this.props.color}
                push
                >
                <Text>{content}</Text>
                <div
                    style={{
                        display: "flex",
                        justifyContent: "center",
                        flexDirection: "column",
                        alignItems: "center"
                    }}
                    >
                    <span style={{color : "#FFF", lineHeight : "30px", fontSize: "16px"}}>
                        数据测量时间： {createtimestring}
                    </span>
                    <div style={{width: "100%",paddingTop:"10px"}} className="mainchart2content">
                        <div style={{width: "50%"}} className="btnlist">
                            <Button style={{width: "80%"}} onClick={() => {this.props.dispatch(getrealtimedata_request({}));}} >
                                开始测量
                            </Button>
                            <Button style={{width: "80%"}}>
                                清空图表
                            </Button>
                            <Button style={{width: "80%"}}>
                                打开串口
                            </Button>
                            <Button style={{width: "80%"}}>
                                关闭串口
                            </Button>
                        </div>
                        <div className="chart2" style={{height : ((window.innerHeight - 150)*.4)+"px"}}>
                            <ChartXY height={ ((window.innerHeight - 150)*.4)} width={ ((window.innerHeight - 150)*.4)} lines={lines} />
                        </div>
                    </div>
                </div>

            </NavPaneItem>
        );
    }

    renderItem2(title, content) {
        return (
            <NavPaneItem
                title={<span style={{color: "#FFF"}}>{title}</span>}
                icon={this.renderIcon(title)}
                theme="light"
                background="#0075c1"
                selected={this.state.selected === title}
                onSelect={() => this.setState({ selected: title })}
                padding="10px 20px"
                color = {this.props.color}
                push
                >
                <Text>{content}</Text>
            </NavPaneItem>
        );
    }

    renderIcon(name) {
        const fill = this.props.theme === 'dark' ? '#ffffff' : '#FFFFFF';
        switch(name) {
            case '工作台':
                return (
                    <img
                        src="data:image/png;base64,"
                        style={{width: "20px"}}
                    />
                );
            case '历史查询':
                return (
                    <img
                        src="data:image/png;base64,"
                        style={{width: "20px"}}
                    />
                );
        }
    }
}

const mapStateToProps = ({serialportdata}) => {
    const {currealtimedatalist} = serialportdata;
    let createtimestring = '';
    if(currealtimedatalist.length > 0){
      createtimestring = _.last(currealtimedatalist).createtimestring;
    }
    return {createtimestring,currealtimedatalist};
};
export default connect(mapStateToProps)(MainPage);
